sp_widget.client_script:
api.controller=function($location, $scope, $timeout, spUtil) {
	/* widget controller */
	var c = this;

	$scope.$watch(function(){
		return $location.search();
	}, function(value){
		console.log(value);
		spUtil.addInfoMessage(JSON.stringify(value))
	})

	$scope.selectTab=function(selectedTab){

		$location.search( _.merge({'id': 'sp_tabbed_tasks', 'spa': 1}, 
															c.data.tabConfig[selectedTab]) );

	}






};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	var showMine = options.showMine || false;
	var query = 'active=true^active=true^EQ';

	data.tabConfig={
		'inc':{
			'table': 'incident',
			'filter': query,
			'view': ''
		},
		'prb':{
			'table': 'problem',
			'filter': query,
			'view': ''
		},
		'chg':{
			'table': 'change_request',
			'filter': query,
			'view': ''
		}
	}

	data.incList = $sp.getWidget('data-table-from-url', data.tabConfig.inc);
	data.prbList = $sp.getWidget('data-table-from-url', data.tabConfig.prb);
	data.chgList = $sp.getWidget('data-table-from-url', data.tabConfig.chg);

})();
